mapper.delete:
DELETE FROM knowledge_category_embeddings
WHERE id IN

    #{id}

mapper.select:
SELECT
id, category_id, model_name, model_version, provider, namespace,
metric, dim, embedding, deleted,
creator, create_time, updater, update_time
FROM knowledge_category_embeddings


        AND category_id = #{categoryId}


        AND model_name = #{modelName}


        AND namespace = #{namespace}


        AND deleted = #{deleted}


ORDER BY id DESC
LIMIT #{pageSize} OFFSET #{(pageNo - 1) * pageSize}

SELECT COUNT(*)
FROM knowledge_category_embeddings


        AND category_id = #{categoryId}


        AND model_name = #{modelName}


        AND namespace = #{namespace}


        AND deleted = #{deleted}
