mapper.insert:
INSERT INTO knowledge_doc_chunks_embeddings (
chunk_id, model_name, model_version, provider, namespace,
metric, dim, embedding, deleted,
creator, create_time, updater, update_time
) VALUES (
#{chunkId}, #{modelName}, #{modelVersion}, #{provider}, #{namespace},
#{metric}, #{dim}, #{embedding}, #{deleted},
#{creator}, #{createTime}, #{updater}, #{updateTime}
)

mapper.select:
SELECT *
FROM knowledge_doc_chunks_embeddings


        AND chunk_id = #{chunkId}


        AND model_name = #{modelName}


        AND namespace = #{namespace}


        AND deleted = #{deleted}


ORDER BY id DESC
LIMIT #{pageSize} OFFSET #{(pageNo - 1) * pageSize}

SELECT COUNT(*)
FROM knowledge_doc_chunks_embeddings


        AND chunk_id = #{chunkId}


        AND model_name = #{modelName}


        AND namespace = #{namespace}


        AND deleted = #{deleted}
