mapper.delete:
DELETE FROM knowledge_faq_category_links
WHERE id IN

    #{id}

mapper.select:
SELECT id, tenant_id, faq_id, category_id, confidence, link_source, deleted, creator, create_time, updater, update_time FROM knowledge_faq_category_links
AND faq_id = #{faqId}
AND category_id = #{categoryId}
AND deleted = #{deleted}
ORDER BY id DESC LIMIT #{pageSize} OFFSET #{(pageNo - 1) * pageSize}

SELECT COUNT(1)
FROM knowledge_faq_category_links


        AND faq_id = #{faqId}


        AND category_id = #{categoryId}


        AND deleted = #{deleted}
