mapper.delete:
DELETE FROM knowledge_faq_embeddings
WHERE id IN

    #{id}

mapper.select:
SELECT id, faq_id, qa_role, model_name, model_version, provider, namespace, metric, dim, embedding, deleted, creator, create_time, updater, update_time FROM knowledge_faq_embeddings
AND faq_id = #{faqId}
AND qa_role = #{qaRole}
AND model_name = #{modelName}
AND namespace = #{namespace}
AND deleted = #{deleted}
ORDER BY id DESC LIMIT #{pageSize} OFFSET #{(pageNo - 1) * pageSize}

SELECT COUNT(*)
FROM knowledge_faq_embeddings


        AND faq_id = #{faqId}


        AND qa_role = #{qaRole}


        AND model_name = #{modelName}


        AND namespace = #{namespace}


        AND deleted = #{deleted}
